import React, { createContext, useContext, useEffect, useState } from 'react'
import { sample_foods } from '../data';


const CartContext = createContext(null)
export default function CartProvider({children}) {

    const [cartItems, setCartItems ] =useState(
        sample_foods
        .slice(1,4)
    .map(food => ({food,quantity:1, price: food.price}))
);
    const [totalPrice,setTotalPrice ] =useState(40);
    const [totalCount,SetTotalCount ] =useState(3);


    useEffect(() =>{
      const totalPrice =sum(carITems.map)(item =>item.price);
      const totalCount = sum(cartItems.map)(item =>item.q)
    },[cartItems]);

    const  sum =items =>{
       items.reduce((prevValue,curValue)=> prevValue + curValue, 0);
    }

    const  removeFromCart = foodId => {
      const filteredCartItems = cartItems.filter(item => item.food.id !== foodId);
      setCartItems(filteredCartItems);
    }


    const changeQuantity = (cartItem,newQuantity)=> {
      const { food } =cartItem;

      const changedCartItem = {
        ...cartItem,
        quantity:newQuantity,
        Price: food.price * newQuantity,
      };
      setCartItems(
       cartItems.map(item =>(item.food.id === food.id ? changedCartItem :item))
      );

    };

  return (
  <CartContext.Provider 
    value={{
      cart:{ items: cartItems,totalPrice, totalCount},
     removeFromCart,
     changeQuantity,
    }}
  >
    {children}
  </CartContext.Provider>
  );
}

export const useCart = ()=> useContext(CartContext)
