import React from 'react';
import { useCart } from '../../hooks/useCart';
import { useAuth } from '../../hooks/useAuth';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { useForm } from'react-hook-form';
import { toast } from'react-toastify';
import { createOrder } from '../../services/orderService';
import classes from './checkoutPage.module.css';
import Tile from '../../components/Title/Tile';;
import Input from '../../components/Input/Input';
import Button from '../../components/Button/Button';
import OrderItemsList from '../../components/OrderItemList/OrderItemList';
export default function CheckoutPage() {
    const { cart } = useCart()
    const { user } = useAuth()
    const navigate = useNavigate();
    const [order, setOrder] = useState({...cart })

      const{
        register,
        formState:{errors},
        handleSubmit
    } = useForm();

    const submit = async data => {
        if(!order.addressLatLng){
            toast.warning('Please select your location on the map')
            return
        }

         await createOrder({...order,name:name, address: data.address});
         navigate('/payment');




        //Clear the cart
        setOrder({})
        toast.success('Order placed successfully')
        navigate('/')

    }

    return (
        <>
          <form onSubmit={handleSubmit(submit)} className={classes.container}>
            <div className={classes.content}>
              <Tile title="Order Form" fontSize="1.6rem" />
              <div className={classes.inputs}>
                <Input
                  defaultValue={user.name}
                  label="Name"
                  {...register('name')}
                  error={errors.name}
                />
                <Input
                  defaultValue={user.address}
                  label="Address"
                  {...register('address')}
                  error={errors.address}
                />
              </div>

              <div>
                <Tile title="Choose Your Location" fontSize="1.6rem" />
                <input type="hidden" id="address" name="address" value={order.address} />
                <div id="map"></div>
              </div>
              <div className={classes.buttons}>
                <div className={classes.buttons}>
                  <Button
                    type="submit"
                    text="GO TO Payment"
                    width="100%"
                    height="3rem"
                  />
                </div>
              </div>
            </div>
          </form>
        </>
      );
}
