import React from 'react'

export default function Price({price,locale ,currency}) {
    const formatPrice =()=>
        new Intl.NumberFormat(locale, {
            style: 'currency',
            currency
          }).format(price);


  return <spn>{formatPrice()}</spn>

};

Price.defaultProps ={
    local: 'en-US',
    currency:'USD',

};
