export default function PaypalButtons({order}) {
  return (
  <PayPalScriptProvider 
  options={{
    clientId:
    'AQrQNJ7YoPjdTD-sCpqNeZIcXLkZ0Xs28GWWVYaNcJQJlJdaV6YPt9TyT7k7C83svACQ7OE3B2UfAQA7'

  }}
  >
    <Buttons order={order} />
  </PayPalScriptProvider>

 );

}
