   useEffect(()=>{
     isPending ? showLoading() : hideLoading();
   });

   const createOrder = (data,action)=> {
    return action.order.create({
      purchase_units: [
        {
          amount: {
            currency_code: 'USD',
            value: order.total,
          },

        },
      ],
    })
   }

